  )
values (#{receiptid,jdbcType=INTEGER}, #{orderid,jdbcType=INTEGER}, #{receiptcount,jdbcType=INTEGER}, 
  #{receiptvolume,jdbcType=DOUBLE}, #{receiptweight,jdbcType=DOUBLE}, #{cargoamount,jdbcType=INTEGER}, 
  #{cargodeduction,jdbcType=DOUBLE}, #{servicededuction,jdbcType=DOUBLE}, #{receiptnote,jdbcType=VARCHAR}, 
  #{returndate,jdbcType=DATE}, #{remarks1,jdbcType=VARCHAR}, #{remarks2,jdbcType=VARCHAR}
  )
